import React, { useState } from "react";
import "./Home.css";

const items = [
  { id: 1, name: "Rice", pricePerKg: 40, image: "https://via.placeholder.com/150", unit: "kg" },
  { id: 2, name: "Wheat Flour", pricePerKg: 50, image: "https://via.placeholder.com/150", unit: "kg" },
  { id: 3, name: "Salt Packet", price: 10, image: "https://via.placeholder.com/150", unit: "packet" },
  { id: 4, name: "Soap", price: 20, image: "https://via.placeholder.com/150", unit: "piece" },
  { id: 5, name: "Sugar", pricePerKg: 45, image: "https://via.placeholder.com/150", unit: "kg" },
];

const Home = () => {
  const [cart, setCart] = useState([]);

  const addToCart = (item, quantity = 1) => {
    const existingItem = cart.find((cartItem) => cartItem.id === item.id);
    if (existingItem) {
      setCart(
        cart.map((cartItem) =>
          cartItem.id === item.id ? { ...cartItem, quantity: cartItem.quantity + quantity } : cartItem
        )
      );
    } else {
      setCart([...cart, { ...item, quantity }]);
    }
  };

  const removeFromCart = (item) => {
    const existingItem = cart.find((cartItem) => cartItem.id === item.id);
    if (existingItem.quantity === 1) {
      setCart(cart.filter((cartItem) => cartItem.id !== item.id));
    } else {
      setCart(
        cart.map((cartItem) =>
          cartItem.id === item.id ? { ...cartItem, quantity: cartItem.quantity - 1 } : cartItem
        )
      );
    }
  };

  const getTotalPrice = () => {
    return cart.reduce((total, item) => {
      const price = item.price || item.pricePerKg;
      return total + price * item.quantity;
    }, 0);
  };

  const shareOnWhatsApp = () => {
    const shopOwnerNumber = "9170"; // Replace with actual number
    const message = cart
      .map((item) => `${item.name} - ${item.quantity} ${item.unit || ""}`)
      .join(", ");
    const totalPrice = getTotalPrice();
    const whatsappLink = `https://wa.me/${shopOwnerNumber}?text=Order Details: ${message}. Total: ₹${totalPrice}`;
    window.open(whatsappLink, "_blank");
  };

  return (
    <div>
      <h2>Kirana Shop</h2>
      <div className="product-list">
        {items.map((item) => (
          <div key={item.id} className="product-card">
            <img src={item.image} alt={item.name} />
            <h3>{item.name}</h3>
            {item.pricePerKg ? (
              <p>
                ₹{item.pricePerKg}/kg | ₹{item.pricePerKg / 2}/half kg
              </p>
            ) : (
              <p>₹{item.price} per {item.unit}</p>
            )}
            <button onClick={() => addToCart(item)}>Add to Cart</button>
          </div>
        ))}
      </div>

      <div className="cart">
        <h3>Cart</h3>
        {cart.map((item) => (
          <div key={item.id} className="cart-item">
            <span>{item.name}</span>
            <button onClick={() => removeFromCart(item)}>-</button>
            <span>{item.quantity}</span>
            <button onClick={() => addToCart(item)}>+</button>
          </div>
        ))}
        <h4>Total: ₹{getTotalPrice()}</h4>
        <button onClick={shareOnWhatsApp}>Share on WhatsApp</button>
      </div>
    </div>
  );
};

export default Home;
