import React, { useState } from "react";
import "./Home.css";

const categories = {
  "Pindi Padarthalu": [
    { id: 1, name: "Rice Flour", pricePerKg: 40, mrp: 45, image: "https://i.imgur.com/th6BLto.jpg" },
    { id: 2, name: "Wheat Flour", pricePerKg: 50, mrp: 55, image: "https://i.imgur.com/th6BLto.jpg" },
  ],
  "₹1 Items": [
    { id: 3, name: "Candy", price: 1, mrp: 1.5, image: "https://i.imgur.com/th6BLto.jpg" },
  ],
  "₹2 Items": [
    { id: 4, name: "Chewing Gum", price: 2, mrp: 2.5, image: "https://i.imgur.com/th6BLto.jpg" },
  ],
  // Add other categories as neededhttps://imgur.com/gallery/bha-iK7jztp
};

const Home = () => {
  const [cart, setCart] = useState([]);

  const addToCart = (item, quantity) => {
    const existingItem = cart.find((cartItem) => cartItem.id === item.id);
    if (existingItem) {
      setCart(
        cart.map((cartItem) =>
          cartItem.id === item.id
            ? { ...cartItem, quantity: cartItem.quantity + quantity }
            : cartItem
        )
      );
    } else {
      setCart([...cart, { ...item, quantity }]);
    }
  };

  const removeFromCart = (item) => {
    const existingItem = cart.find((cartItem) => cartItem.id === item.id);
    if (existingItem.quantity === 1) {
      setCart(cart.filter((cartItem) => cartItem.id !== item.id));
    } else {
      setCart(
        cart.map((cartItem) =>
          cartItem.id === item.id
            ? { ...cartItem, quantity: cartItem.quantity - 1 }
            : cartItem
        )
      );
    }
  };

  const getTotalPrice = () => {
    return cart.reduce((total, item) => {
      const price = item.price || item.pricePerKg || item.pricePerLitre;
      return total + price * item.quantity;
    }, 0);
  };

  const shareOnWhatsApp = () => {
    const shopOwnerNumber = "917036783962"; // Replace with actual number
    const message = cart
      .map((item) => `${item.name} - ${item.quantity} ${item.unit || "unit"}`)
      .join(", ");
    const totalPrice = getTotalPrice();
    const whatsappLink = `https://wa.me/${shopOwnerNumber}?text=Order Details: ${message}. Total: ₹${totalPrice}`;
    window.open(whatsappLink, "_blank");
  };

  return (
    <div>
      {/* Fixed Header Section */}
      <div className="header">
        <div className="shop-infofixed-top">
          <img src="https://i.imgur.com/th6BLto.jpg" alt="Shop Logo" className="shop-logo" />
          <div className="shop-details">
            <h1>Lakshana spices</h1>
            <h3>Owner: Ramesh Kumar</h3>
            <p>Phone: 7036783962</p>
            <div className="contact-icons">
              <a href="mailto:villagekiranashop@example.com" className="icon email-icon">📧</a>
              <a href="https://maps.google.com?q=Village+Kirana+Shop" target="blank" className="icon map-icon">📍</a>
              <a href="https://wa.me/917036783962" target="" className="icon whatsapp-icon">💬</a>
              <a href="tel:7036783962" className="icon call-icon">📞</a>
            </div>
          </div>
        </div>
        <div className="banner">
          <h2>🎉 Free Door Delivery 🎉</h2>
        </div>
      </div>

      {/* Product Categories */}
      {Object.keys(categories).map((category) => (
        <div key={category} className="category">
          <h2>{category}</h2>
          <div className="product-list">
            {categories[category].map((item) => (
              <div key={item.id} className="product-card">
                <img src={item.image} alt={item.name} />
                <h3>{item.name}</h3>
                <p>Price: ₹{item.price} <span className="mrp">₹{item.mrp}</span></p>
                <button onClick={() => addToCart(item, 1)}>Add to Cart</button>
              </div>
            ))}
          </div>
        </div>
      ))}

      {/* Cart Section */}
      <div className="cart">
        <h3>Cart</h3>
        {cart.map((item) => (
          <div key={item.id} className="cart-item">
            <span>{item.name}</span>
            <button onClick={() => removeFromCart(item)}>-</button>
            <span>{item.quantity}</span>
            <button onClick={() => addToCart(item, 1)}>+</button>
          </div>
        ))}
        <h4>Total: ₹{getTotalPrice()}</h4>
        <button onClick={shareOnWhatsApp}>Share on WhatsApp</button>
      </div>

      {/* Footer Section */}
      <footer className="footer">
        <p>Address: Village Kirana Shop, Main Road, Hyderabad</p>
        <p>Pincode: 500001</p>
        <p>&copy; 2024 E-Shop. All rights reserved.</p>
  
      </footer>
    </div>
    
  );
};

export default Home;
