import React, { useState } from "react";
import "./Home.css";

const categories = {
  "Pindi Padarthalu": [
    { id: 1, name: "Rice Flour", pricePerKg: 40, image: "https://via.placeholder.com/150" },
    { id: 2, name: "Wheat Flour", pricePerKg: 50, image: "https://via.placeholder.com/150" },
  ],
  "₹1 Items": [
    { id: 3, name: "Candy", price: 1, image: "https://via.placeholder.com/150" },
  ],
  "₹2 Items": [
    { id: 4, name: "Chewing Gum", price: 2, image: "https://via.placeholder.com/150" },
  ],
  "₹5 Items": [
    { id: 5, name: "Salt Packet", price: 5, image: "https://via.placeholder.com/150" },
  ],
  "₹10 Items": [
    { id: 6, name: "Match Box", price: 10, image: "https://via.placeholder.com/150" },
  ],
  "₹20 Items": [
    { id: 7, name: "Cookies", price: 20, image: "https://via.placeholder.com/150" },
  ],
  "Liquid Items": [
    { id: 8, name: "Cooking Oil", pricePerLitre: 150, image: "https://via.placeholder.com/150" },
  ],
  "Pappulu": [
    { id: 9, name: "Toor Dal", pricePerKg: 80, image: "https://via.placeholder.com/150" },
    { id: 10, name: "Moong Dal", pricePerKg: 90, image: "https://via.placeholder.com/150" },
  ],
  Soaps: [
    { id: 11, name: "Rin", price: 25, image: "https://via.placeholder.com/150" },
    { id: 12, name: "Santoor", price: 30, image: "https://via.placeholder.com/150" },
  ],
};

const Home = () => {
  const [cart, setCart] = useState([]);

  const addToCart = (item, quantity) => {
    const existingItem = cart.find((cartItem) => cartItem.id === item.id);
    if (existingItem) {
      setCart(
        cart.map((cartItem) =>
          cartItem.id === item.id ? { ...cartItem, quantity: cartItem.quantity + quantity } : cartItem
        )
      );
    } else {
      setCart([...cart, { ...item, quantity }]);
    }
  };

  const removeFromCart = (item) => {
    const existingItem = cart.find((cartItem) => cartItem.id === item.id);
    if (existingItem.quantity === 1) {
      setCart(cart.filter((cartItem) => cartItem.id !== item.id));
    } else {
      setCart(
        cart.map((cartItem) =>
          cartItem.id === item.id ? { ...cartItem, quantity: cartItem.quantity - 1 } : cartItem
        )
      );
    }
  };

  const getTotalPrice = () => {
    return cart.reduce((total, item) => {
      const price = item.price || item.pricePerKg || item.pricePerLitre;
      return total + price * item.quantity;
    }, 0);
  };

  const shareOnWhatsApp = () => {
    const shopOwnerNumber = "919876543210"; // Replace with actual number
    const message = cart
      .map((item) => `${item.name} - ${item.quantity} ${item.unit || "unit"}`)
      .join(", ");
    const totalPrice = getTotalPrice();
    const whatsappLink = `https://wa.me/${shopOwnerNumber}?text=Order Details: ${message}. Total: ₹${totalPrice}`;
    window.open(whatsappLink, "_blank");
  };

  return (
    <div>
      <h1>Village Kirana Shop</h1>
      {Object.keys(categories).map((category) => (
        <div key={category}>
          <h2>{category}</h2>
          <div className="product-list">
            {categories[category].map((item) => (
              <div key={item.id} className="product-card">
                <img src={item.image} alt={item.name} />
                <h3>{item.name}</h3>
                {item.pricePerKg ? (
                  <div>
                    <p>₹{item.pricePerKg}/kg</p>
                    <p>₹{item.pricePerKg / 2}/half kg</p>
                    <button onClick={() => addToCart(item, 0.5)}>Add 1/2 kg</button>
                    <button onClick={() => addToCart(item, 1)}>Add 1 kg</button>
                  </div>
                ) : (
                  <p>₹{item.price} per {item.unit}</p>
                )}
                {!item.pricePerKg && (
                  <button onClick={() => addToCart(item, 1)}>Add to Cart</button>
                )}
              </div>
            ))}
          </div>
        </div>
      ))}
      <div className="cart">
        <h3>Cart</h3>
        {cart.map((item) => (
          <div key={item.id} className="cart-item">
            <span>{item.name}</span>
            <button onClick={() => removeFromCart(item)}>-</button>
            <span>{item.quantity}</span>
            <button onClick={() => addToCart(item, 1)}>+</button>
          </div>
        ))}
        <h4>Total: ₹{getTotalPrice()}</h4>
        <button onClick={shareOnWhatsApp}>Share on WhatsApp</button>
      </div>
    </div>
  );
};

export default Home;
