import React, { useState } from "react";
import "./Home.css";

const categories = {
  "Pindi Padarthalu": [
    { id: 1, name: "Wheat Flour", pricePerKg: 50, image: "https://via.placeholder.com/150" },
    { id: 2, name: "Rice Flour", pricePerKg: 45, image: "https://via.placeholder.com/150" },
  ],
  "₹1 Items": [
    { id: 3, name: "Candy", price: 1, image: "https://via.placeholder.com/150" },
  ],
  "₹2 Items": [
    { id: 4, name: "Chewing Gum", price: 2, image: "https://via.placeholder.com/150" },
  ],
  "₹5 Items": [
    { id: 5, name: "Salt Packet", price: 5, image: "https://via.placeholder.com/150" },
  ],
  "₹10 Items": [
    { id: 6, name: "Match Box", price: 10, image: "https://via.placeholder.com/150" },
  ],
  "₹20 Items": [
    { id: 7, name: "Cookies", price: 20, image: "https://via.placeholder.com/150" },
  ],
  "Liquid Items": [
    { id: 8, name: "Oil", pricePerLitre: 150, image: "https://via.placeholder.com/150" },
  ],
  "Pappulu": [
    { id: 9, name: "Toor Dal", pricePerKg: 80, image: "https://via.placeholder.com/150" },
  ],
  Soaps: [
    { id: 10, name: "Rin", price: 25, image: "https://via.placeholder.com/150" },
    { id: 11, name: "Santoor", price: 30, image: "https://via.placeholder.com/150" },
  ],
};

const Home = () => {
  const [cart, setCart] = useState([]);

  const addToCart = (item, quantity = 1) => {
    const existingItem = cart.find((cartItem) => cartItem.id === item.id);
    if (existingItem) {
      setCart(
        cart.map((cartItem) =>
          cartItem.id === item.id ? { ...cartItem, quantity: cartItem.quantity + quantity } : cartItem
        )
      );
    } else {
      setCart([...cart, { ...item, quantity }]);
    }
  };
 

  const shareOnWhatsApp = () => {
    const shopOwnerNumber = "917036783962"; // Replace with actual number
    const message = cart
      .map((item) => `${item.name} - ${item.quantity} ${item.unit || ""}`)
      .join(", ");
    const totalPrice = getTotalPrice();
    const whatsappLink = `https://wa.me/${shopOwnerNumber}?text=Order Details: ${message}. Total: ₹${totalPrice}`;
    window.open(whatsappLink, "_blank");
  };

  return (
    <div>
      <h2>Kirana Shop</h2>
      <div className="product-list">
        {items.map((item) => (
          <div key={item.id} className="product-card">
            <img src={item.image} alt={item.name} />
            <h3>{item.name}</h3>
            {item.pricePerKg ? (
              <p>
                ₹{item.pricePerKg}/kg | ₹{item.pricePerKg / 2}/half kg
              </p>
            ) : (
              <p>₹{item.price} per {item.unit}</p>
            )}
            <button onClick={() => addToCart(item)}>Add to Cart</button>
          </div>
        ))}
      </div>

      <div className="cart">
        <h3>Cart</h3>
        {cart.map((item) => (
          <div key={item.id} className="cart-item">
            <span>{item.name}</span>
            <button onClick={() => removeFromCart(item)}>-</button>
            <span>{item.quantity}</span>
            <button onClick={() => addToCart(item)}>+</button>
          </div>
        ))}
        <h4>Total: ₹{getTotalPrice()}</h4>
        <button onClick={shareOnWhatsApp}>Share on WhatsApp</button>
      </div>
    </div>
  );
};

export default Home;
